sprites.onOverlap(SpriteKind.Player, SpriteKind.Enemy, function (sprite, otherSprite) {
    if (hit == true) {
        music.play(music.createSoundEffect(WaveShape.Triangle, 200, 1, 255, 255, 100, SoundExpressionEffect.None, InterpolationCurve.Curve), music.PlaybackMode.UntilDone)
        scene.cameraShake(4, 500)
        hearts += -1
        refresh_hearts()
        if (hearts == 0) {
            game.gameOver(false)
        }
        pause(1000)
    } else if (hit == false) {
        hearts += 0
    }
})
